import React, { useState } from "react";
import { Link } from "react-router-dom";

function Home() {
  const [role, setRole] = useState("");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [loginMsg, setLoginMsg] = useState("");

  const handleLogin = () => {
    // 这里只做简单演示，实际项目请用后端校验
    if (role === "admin" && username === "admin" && password === "admin123") {
      setLoginMsg("管理者登入成功！");
    } else if (role === "dealer" && username === "dealer" && password === "dealer123") {
      setLoginMsg("通路商登入成功！");
    } else {
      setLoginMsg("帳號或密碼錯誤");
    }
  };

  return (
    <div>
      <h2>恆通經銷車用零件管理系統</h2>
      <div style={{ background: "#222", color: "#fff", padding: 24, borderRadius: 8, marginBottom: 24, width: 320 }}>
        <h3>登入</h3>
        <div>
          <label>
            <input type="radio" name="role" value="admin" checked={role === "admin"} onChange={() => setRole("admin")}/> 管理者
          </label>
          <label style={{ marginLeft: 16 }}>
            <input type="radio" name="role" value="dealer" checked={role === "dealer"} onChange={() => setRole("dealer")}/> 通路商
          </label>
        </div>
        <input style={{ marginTop: 12, width: "100%" }} type="text" placeholder="帳號" value={username} onChange={e => setUsername(e.target.value)} />
        <input style={{ marginTop: 12, width: "100%" }} type="password" placeholder="密碼" value={password} onChange={e => setPassword(e.target.value)} />
        <button style={{ marginTop: 16, width: "100%" }} onClick={handleLogin}>登入</button>
        {loginMsg && <div style={{ marginTop: 8 }}>{loginMsg}</div>}
      </div>
      <Link to="/inventory">進入庫存管理</Link>
      <br />
      <Link to="/admin">
        <button style={{ marginTop: 16 }}>進入後台</button>
        <Link to="/shipping"><button style={{ marginLeft: 16 }}>出貨統計</button></Link>
      </Link>
    </div>
  );
}
export default Home;
