import React, { useState } from 'react';

function ShippingStats() {
  const [date, setDate] = useState('');
  const [quantity, setQuantity] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    // 假設你的平台有一個 API 可以接收資料
    const res = await fetch('https://你的平台網址/api/shipping', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ date, quantity })
    });
    if (res.ok) {
      setMessage('資料已送出！');
      setDate('');
      setQuantity('');
    } else {
      setMessage('送出失敗，請稍後再試。');
    }
  };

  return (
    <div style={{ maxWidth: 400, margin: '40px auto', padding: 24, border: '1px solid #ccc', borderRadius: 8 }}>
      <h2 style={{ textAlign: 'center' }}>出貨統計</h2>
      <form onSubmit={handleSubmit}>
        <div style={{ marginBottom: 16 }}>
          <label>出貨日期：</label>
          <input type="date" value={date} onChange={e => setDate(e.target.value)} required style={{ width: '100%' }} />
        </div>
        <div style={{ marginBottom: 16 }}>
          <label>出貨數量：</label>
          <input type="number" value={quantity} onChange={e => setQuantity(e.target.value)} required style={{ width: '100%' }} />
        </div>
        <button type="submit" style={{ width: '100%' }}>送出</button>
      </form>
      {message && <div style={{ marginTop: 16, color: 'green' }}>{message}</div>}
    </div>
  );
}

export default ShippingStats;
