import React, { useState } from "react";
import { Link } from "react-router-dom";

function Home() {
  const [role, setRole] = useState("");
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [loginMsg, setLoginMsg] = useState("");

  const handleLogin = () => {
    // 这里只做简单演示，实际项目请用后端校验
    if (role === "admin" && username === "admin" && password === "admin123") {
      setLoginMsg("管理者登入成功！");
    } else if (role === "dealer" && username === "dealer" && password === "dealer123") {
      setLoginMsg("通路商登入成功！");
    } else {
      setLoginMsg("帳號或密碼錯誤");
    }
  };

  return (
    <div>
      <h2>恆通經銷車用零件管理系統</h2>
      <div style={{ background: "#222", color: "#fff", padding: 24, borderRadius: 8, width: 320 }}>
        <h3>登入</h3>
        <div>
          <select value={role} onChange={e => setRole(e.target.value)} style={{ marginBottom: 8, width: "100%" }}>
            <option value="">選擇角色</option>
            <option value="admin">管理者</option>
            <option value="dealer">通路商</option>
          </select>
        </div>
        <div>
          <input
            type="text"
            placeholder="帳號"
            value={username}
            onChange={e => setUsername(e.target.value)}
            style={{ marginBottom: 8, width: "100%" }}
          />
        </div>
        <div>
          <input
            type="password"
            placeholder="密碼"
            value={password}
            onChange={e => setPassword(e.target.value)}
            style={{ marginBottom: 8, width: "100%" }}
          />
        </div>
        <button onClick={handleLogin} style={{ width: "100%" }}>登入</button>
        <div style={{ marginTop: 8 }}>{loginMsg}</div>
      </div>
    </div>
  );
}
export default Home;
