import React, { useState } from "react";
import { Link } from "react-router-dom";

function Home() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [loginMsg, setLoginMsg] = useState("");

  const handleLogin = () => {
    // 範例：依帳號判斷權限
    if (username === "admin" && password === "admin123") {
      setLoginMsg("管理者登入成功！");
      // 可在此存 localStorage 權限資訊
    } else if (username === "dealer" && password === "dealer123") {
      setLoginMsg("通路商登入成功！");
      // 可在此存 localStorage 權限資訊
    } else {
      setLoginMsg("帳號或密碼錯誤");
    }
  };

  return (
    <div>
      <h2>恆通經銷車用零件管理系統</h2>
      <div style={{ background: "#222", color: "#fff", padding: 24, borderRadius: 8, width: 320 }}>
        <h3>登入</h3>
        <div>
          <input
            type="text"
            placeholder="帳號"
            value={username}
            onChange={e => setUsername(e.target.value)}
            style={{ marginBottom: 8, width: "100%" }}
          />
        </div>
        <div>
          <input
            type="password"
            placeholder="密碼"
            value={password}
            onChange={e => setPassword(e.target.value)}
            style={{ marginBottom: 8, width: "100%" }}
          />
        </div>
        <button onClick={handleLogin} style={{ width: "100%" }}>登入</button>
        <div style={{ marginTop: 8 }}>{loginMsg}</div>
      </div>
    </div>
  );
}
export default Home;
