// src/pages/ShippingStats.js
import React, { useState } from 'react';

const mockData = [
  { id: 1, name: "PO-9001" },
  { id: 2, name: "PO-9002" },
  { id: 3, name: "PO-9003" },
  { id: 4, name: "PO-9004" },
  { id: 5, name: "PO-9005" },
  { id: 6, name: "PO-9006" },
  { id: 7, name: "PO-9007" },
  { id: 8, name: "PO-9008" },
  { id: 9, name: "PO-9009" },
  { id: 10, name: "PO-9010" },
  { id: 11, name: "PO-9011" },
  { id: 12, name: "PO-9012" },
  { id: 13, name: "PO-9013" },
  { id: 14, name: "PO-9014" },
  { id: 15, name: "PO-9021" },
  { id: 16, name: "PO-9022" }
];

function getToday() {
  const d = new Date();
  return d.toISOString().slice(0, 10);
}

function ShippingStats() {
  const [shippingData, setShippingData] = useState(
    mockData.map(item => ({ ...item, quantity: '', date: getToday() }))
  );

  const handleChange = (idx, field, value) => {
    const newData = [...shippingData];
    newData[idx][field] = value;
    setShippingData(newData);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // 這裡可以將 shippingData 傳送到後端
    alert(JSON.stringify(shippingData, null, 2));
  };

  return (
    <div style={{ maxWidth: 500, margin: '0 auto', padding: 16 }}>
      <h2 style={{ textAlign: 'center' }}>出貨統計</h2>
      <form onSubmit={handleSubmit}>
        <table style={{ width: '100%' }}>
          <thead>
            <tr>
              <th>品號</th>
              <th>出貨數量</th>
              <th>出貨日期</th>
            </tr>
          </thead>
          <tbody>
            {shippingData.map((item, idx) => (
              <tr key={item.id}>
                <td>{item.name}</td>
                <td>
                  <input
                    type="number"
                    min="0"
                    value={item.quantity}
                    onChange={e => handleChange(idx, 'quantity', e.target.value)}
                    style={{ width: 60 }}
                  />
                </td>
                <td>
                  <input
                    type="date"
                    value={item.date}
                    onChange={e => handleChange(idx, 'date', e.target.value)}
                  />
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <div style={{ textAlign: 'center', marginTop: 16 }}>
          <button type="submit">送出</button>
        </div>
      </form>
    </div>
  );
}

export default ShippingStats;
