// src/pages/ShippingStats.js
import React, { useState } from 'react';

const mockData = [
  { id: 1, name: "PO-9001", image: "images/PO-9001.jpg" },
  { id: 2, name: "PO-9002", image: "images/PO-9002.jpg" },
  { id: 3, name: "PO-9003", image: "images/PO-9003.jpg" },
  { id: 4, name: "PO-9004", image: "images/PO-9004.jpg" },
  { id: 5, name: "PO-9005", image: "images/PO-9005.jpg" },
  { id: 6, name: "PO-9006", image: "images/PO-9006.jpg" },
  { id: 7, name: "PO-9008", image: "images/PO-9008.jpg" },
  { id: 8, name: "PO-9009", image: "images/PO-9009.jpg" },
  { id: 9, name: "PO-9010", image: "images/PO-9010.jpg" },
  { id: 10, name: "PO-9011", image: "images/PO-9011.jpg" },
  { id: 11, name: "PO-9013", image: "images/PO-9013.jpg" },
  { id: 12, name: "PO-9015", image: "images/PO-9015.jpg" },
  { id: 13, name: "PO-9016", image: "images/PO-9016.jpg" },
  { id: 14, name: "PO-9018", image: "images/PO-9018.jpg" },
  { id: 15, name: "PO-9021", image: "images/PO-9021.jpg" },
  { id: 16, name: "PO-9022", image: "images/PO-9022.jpg" }
];

function getToday() {
  const d = new Date();
  return d.toISOString().slice(0, 10);
}

function ShippingStats() {
  const [shippingData, setShippingData] = useState(
    mockData.map(item => ({ ...item, quantity: '' }))
  );
  const today = getToday();

  const handleChange = (idx, value) => {
    const newData = [...shippingData];
    newData[idx].quantity = value;
    setShippingData(newData);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // 這裡可以將 shippingData 傳送到後端
    alert(JSON.stringify({ date: today, data: shippingData }, null, 2));
  };

  return (
    <div style={{ maxWidth: 600, margin: '0 auto', padding: 16 }}>
      <h2 style={{ textAlign: 'center' }}>銷售統計</h2>
      <div style={{ textAlign: 'center', marginBottom: 16, fontWeight: 'bold', fontSize: 18 }}>
        出貨日期：{today}
      </div>
      <form onSubmit={handleSubmit}>
        <table style={{ width: '100%' }}>
          <thead>
            <tr>
              <th>圖片</th>
              <th>品號</th>
              <th>出貨數量</th>
            </tr>
          </thead>
          <tbody>
            {shippingData.map((item, idx) => (
              <tr key={item.id}>
                <td><img src={item.image} alt={item.name} style={{ width: 60, height: 60, objectFit: 'cover' }} /></td>
                <td>{item.name}</td>
                <td>
                  <input
                    type="number"
                    min="0"
                    value={item.quantity}
                    onChange={e => handleChange(idx, e.target.value)}
                    style={{ width: 60 }}
                  />
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        <div style={{ textAlign: 'center', marginTop: 16 }}>
          <button type="submit">送出</button>
        </div>
      </form>
    </div>
  );
}

export default ShippingStats;
