import './App.css';
import Home from "./pages/Home";
import Inventory from "./pages/Inventory";
import Admin from "./pages/Admin";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import ShippingStats from './pages/ShippingStats';
import { partsData } from "./pages/partsData";
import React, { useState, useEffect } from "react";
import Register from "./pages/Register";
import { UserProvider, UserContext } from "./UserContext";

function App() {
  // 初始化时优先从 localStorage 读取
  const getInitialParts = () => {
    const saved = localStorage.getItem("parts");
    if (saved) {
      try {
        return JSON.parse(saved);
      } catch {
        return partsData;
      }
    }
    return partsData;
  };
  const [parts, setParts] = useState(getInitialParts());

  // 每次 parts 变化时保存到 localStorage
  useEffect(() => {
    localStorage.setItem("parts", JSON.stringify(parts));
  }, [parts]);
  return (
    <UserProvider>
      <Router>
        <div className="App">
          <UserContext.Consumer>
            {({ user }) => (
              <div style={{ position: 'absolute', top: 16, right: 24, color: '#333', fontWeight: 'bold' }}>
                {user ? `登入者：${user.company || user.username}` : '未登入'}
              </div>
            )}
          </UserContext.Consumer>
          <Routes>
            <Route path="/home" element={<Home />} />
            <Route path="/" element={<Navigate to="/home" replace />} />
            <Route path="/inventory" element={<Inventory parts={parts} setParts={setParts} />} />
            <Route path="/admin" element={<Admin />} />
            <Route path="/shipping" element={<ShippingStats parts={parts} setParts={setParts} />} />
            <Route path="/register" element={<Register />} />
          </Routes>
        </div>
      </Router>
    </UserProvider>
  );
}

export default App;
