import React, { useContext, useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { UserContext } from "../UserContext";

function Admin() {
  const navigate = useNavigate();
  const { setUser } = useContext(UserContext);
  const [orders, setOrders] = useState([]);
  useEffect(() => {
    const interval = setInterval(() => {
      const data = JSON.parse(localStorage.getItem("orders") || "[]");
      setOrders(data.reverse()); // 最新在上
    }, 1000); // 每秒自動更新
    return () => clearInterval(interval);
  }, []);
  return (
    <div style={{ display: 'flex', flexDirection: 'row' }}>
      {/* 左側提醒區塊 */}
      <div style={{ minWidth: 320, background: '#f8f8f8', padding: 16, marginRight: 24, borderRadius: 8, height: 'fit-content' }}>
        <h3 style={{ marginTop: 0 }}>最新出貨提醒</h3>
        <ul style={{ paddingLeft: 16, maxHeight: 400, overflowY: 'auto', margin: 0 }}>
          {orders.length === 0 && <li>暫無出貨紀錄</li>}
          {orders.map((o, idx) => (
            <li key={idx} style={{ marginBottom: 8, fontSize: 15 }}>
              <b>{o.company}</b> 於 <span style={{ color: '#888' }}>{o.time}</span><br />
              商品：<b>{o.partName}</b>，數量：<b>{o.quantity}</b>
            </li>
          ))}
        </ul>
      </div>
      {/* 右側原本內容 */}
      <div>
        <h2>後台管理系統</h2>
        <div style={{ display: "flex", flexDirection: "column", gap: 16, marginTop: 32 }}>
          <button style={{ padding: 16, fontSize: 18 }} onClick={() => navigate("/inventory")}>庫存管理</button>
          <button style={{ padding: 16, fontSize: 18 }} onClick={() => navigate("/shipping")}>出貨統計</button>
          <button style={{ padding: 16, fontSize: 18 }}>通路商帳號管理</button>
          <button style={{ padding: 16, fontSize: 18 }}>資料備份/還原</button>
        </div>
        <div style={{ display: 'flex', justifyContent: 'center', marginTop: 24 }}>
          <button onClick={() => {
            localStorage.removeItem("user");
            setUser(null);
            navigate("/");
          }} style={{ width: 120, background: '#c00', color: '#fff', border: 'none', borderRadius: 6, padding: 8 }}>
            登出
          </button>
        </div>
      </div>
    </div>
  );
}

export default Admin;
