import React, { useContext, useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { UserContext } from "../UserContext";

function Admin() {
  const navigate = useNavigate();
  const { setUser } = useContext(UserContext);
  const [orders, setOrders] = useState([]);
  useEffect(() => {
    const interval = setInterval(() => {
      const data = JSON.parse(localStorage.getItem("records") || "[]");
      setOrders(data.reverse()); // 最新在上
    }, 1000); // 每秒自動更新
    return () => clearInterval(interval);
  }, []);
  return (
    <div style={{ display: 'flex', flexDirection: 'row', justifyContent: 'center', alignItems: 'flex-start', width: '100%' }}>
      {/* 左側提醒區塊 */}
      <div style={{ minWidth: 400, maxWidth: 500, width: '32vw', background: '#23272f', padding: 24, marginRight: 40, borderRadius: 12, height: 'fit-content', color: '#f5f6fa', boxShadow: '0 2px 22px #0002' }}>
        <h3 style={{ marginTop: 10, color: '#f5f6fa' }}>最新出貨提醒</h3>
        <ul style={{ paddingLeft: 16, maxHeight: 600, overflowY: 'auto', margin: 0 }}>
          {orders.length === 0 && <li style={{ color: '#aaa' }}>暫無出貨紀錄</li>}
          {orders.map((o, idx) => (
            <li key={idx} style={{ marginBottom: 8, fontSize: 15, color: '#f5f6fa' }}>
              <b>{o.company}</b> 於 <span style={{ color: '#aaa' }}>{o.time}</span><br />
              商品：<b>{o.partName}</b>，數量：<b>{o.quantity}</b>
            </li>
          ))}
        </ul>
      </div>
      {/* 右側後台內容區塊 */}
      <div style={{ flex: 1, display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center' }}>
        {/* 這裡放你的後台管理系統內容，會自動置中 */}
        <h2>後台管理系統</h2>
        <div style={{ display: "flex", flexDirection: "column", gap: 16, marginTop: 32 }}>
          <button style={{ padding: 16, fontSize: 18 }} onClick={() => navigate("/inventory")}>庫存管理</button>
          <button style={{ padding: 16, fontSize: 18 }} onClick={() => navigate("/shipping")}>出貨統計</button>
          <button style={{ padding: 16, fontSize: 18 }}>通路商帳號管理</button>
          <button style={{ padding: 16, fontSize: 18 }}>資料備份/還原</button>
        </div>
        <div style={{ display: 'flex', justifyContent: 'center', marginTop: 24 }}>
          <button onClick={() => {
            localStorage.removeItem("user");
            setUser(null);
            navigate("/");
          }} style={{ width: 120, background: '#c00', color: '#fff', border: 'none', borderRadius: 6, padding: 8 }}>
            登出
          </button>
        </div>
      </div>
    </div>
  );
}

export default Admin;
