import React, { useState } from "react";
import { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { dealersData } from "./dealersData";

function Home() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [loginMsg, setLoginMsg] = useState("");
  const [registerInfo, setRegisterInfo] = useState({ username: "", password: "", name: "", email: "", phone: "" });
  const [registerMsg, setRegisterMsg] = useState("");
  const navigate = useNavigate();
  useEffect(() => {
    const user = JSON.parse(localStorage.getItem("user"));
    if (!user) {
      navigate("/Home");
    }
  }, [navigate]);

  const handleLogin = () => {
    if (username === "admin" && password === "admin123") {
      setLoginMsg("管理者登入成功！");
      setTimeout(() => navigate("/admin"), 800); // 0.8秒後跳轉
    } else if (username === "dealer" && password === "dealer123") {
      setLoginMsg("通路商登入成功！");
      setTimeout(() => navigate("/shipping"), 800);
    } else {
      setLoginMsg("帳號或密碼錯誤");
    }
  };

  const handleRegisterChange = (e) => {
    setRegisterInfo({ ...registerInfo, [e.target.name]: e.target.value });
  };

  const handleRegister = (e) => {
    e.preventDefault();
    // 檢查帳號是否重複
    if (dealersData.some(d => d.username === registerInfo.username)) {
      setRegisterMsg("帳號已存在，請更換帳號");
      return;
    }
    // 新增到 dealersData（僅前端模擬，實際應呼叫後端）
    dealersData.push({
      id: dealersData.length + 1,
      ...registerInfo,
      status: "active"
    });
    setRegisterMsg("申請成功，請等待審核或直接登入");
    setRegisterInfo({ username: "", password: "", name: "", email: "", phone: "" });
  };

  return (
    <div>
      <h2>恆通經銷車用零件管理系統</h2>
      <div style={{ background: "#222", color: "#fff", padding: 24, borderRadius: 8, width: 320 }}>
        <h3>登入</h3>
        <div>
          <input
            type="text"
            placeholder="帳號"
            value={username}
            onChange={e => setUsername(e.target.value)}
            style={{ marginBottom: 8, width: "100%" }}
          />
        </div>
        <div>
          <input
            type="password"
            placeholder="密碼"
            value={password}
            onChange={e => setPassword(e.target.value)}
            style={{ marginBottom: 8, width: "100%" }}
          />
        </div>
        <button onClick={handleLogin} style={{ width: "100%" }}>登入</button>
        <div style={{ marginTop: 8 }}>{loginMsg}</div>
        <div style={{ marginTop: 32, textAlign: 'center' }}>
          <button onClick={() => navigate('/register')}>申請通路商帳號</button>
        </div>
        {/* 申請表單已移除，僅保留按鈕 */}
        <div style={{ color: 'red', marginTop: 8 }}>{registerMsg}</div>
      </div>
    </div>
  );
}
export default Home;
