import Home from "./pages/Home";
import Inventory from "./pages/Inventory";
import Admin from "./pages/Admin";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import ShippingStats from './pages/ShippingStats';
import partsData from "./partsData";
import React, { useState } from "react";

function App() {
  // 初始化时优先从 localStorage 读取
  const getInitialParts = () => {
    const saved = localStorage.getItem("parts");
    if (saved) {
      try {
        return JSON.parse(saved);
      } catch {
        return partsData;
      }
    }
    return partsData;
  };
  const [parts, setParts] = useState(getInitialParts());

  // 每次 parts 变化时保存到 localStorage
  useEffect(() => {
    localStorage.setItem("parts", JSON.stringify(parts));
  }, [parts]);
  return (
    <Router>
      <div className="App">
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/inventory" element={<Inventory parts={parts} setParts={setParts} />} />
          <Route path="/admin" element={<Admin />} />
          <Route path="/shipping" element={<ShippingStats parts={parts} setParts={setParts} />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
