import React, { useEffect, useState } from "react";
import axios from "axios";

function Inventory() {
  const [parts, setParts] = useState([]);
  const [search, setSearch] = useState("");

  useEffect(() => {
    fetchParts();
  }, []);

  const fetchParts = async () => {
    // 假資料
    const mockData = [
      { id: 1, name: "PO-9001", stock: 50 },
      { id: 2, name: "PO-9002", stock: 120 },
      { id: 3, name: "PO-9003", stock: 80 },
      { id: 4, name: "PO-9004", stock: 80 },
      { id: 5, name: "PO-9005", stock: 80 },
      { id: 6, name: "PO-9006",  stock: 80 },
      { id: 7, name: "PO-9008",  stock: 80 },
      { id: 8, name: "PO-9009",  stock: 80 },
      { id: 9, name: "PO-9010", stock: 80 },
      { id: 10, name: "PO-9011", stock: 80 },
      { id: 11, name: "PO-9013", stock: 80 },
      { id: 12, name: "PO-9015", stock: 80 },
      { id: 13, name: "PO-9016", stock: 80 },
      { id: 14, name: "PO-9018", stock: 80 },
      { id: 15, name: "PO-9021", stock: 80 },
      { id: 16, name: "PO-9022", stock: 80 },














    ];
    setParts(mockData);
  };

  const handleSearch = (e) => {
    setSearch(e.target.value);
  };

  const filteredParts = parts.filter(
    (part) =>
      part.name.toLowerCase().includes(search.toLowerCase()) ||
      part.type.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div>
      <h2>庫存管理</h2>
      <input
        type="text"
        placeholder="搜尋零件名稱或類型"
        value={search}
        onChange={handleSearch}
        style={{ marginBottom: 16, padding: 8, width: 300 }}
      />
      <table border="1" cellPadding="8" style={{ width: "100%", marginTop: 16 }}>
        <thead>
          <tr>
            <th>名稱</th>
            <th>庫存數量</th>
            <th>進價</th>
            <th>售價</th>
            <th>操作</th>
          </tr>
        </thead>
        <tbody>
          {filteredParts.map((part) => (
            <tr key={part.id}>
              <td>{part.name}</td>
              <td>{part.stock}</td>
              <td>{part.stock}</td>
              <td>{part.price}</td>
              <td>
                <button>編輯</button>
                <button style={{ marginLeft: 8 }}>刪除</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Inventory;
