import React, { useEffect, useState } from "react";
import axios from "axios";

function Inventory() {
  const [parts, setParts] = useState([]);
  const [search, setSearch] = useState("");

  useEffect(() => {
    fetchParts();
  }, []);

  const fetchParts = async () => {
    // TODO: 請將 URL 換成你的後端 API 端點
    const res = await axios.get("http://localhost:8080/api/carparts");
    setParts(res.data);
  };

  const handleSearch = (e) => {
    setSearch(e.target.value);
  };

  const filteredParts = parts.filter(
    (part) =>
      part.name.toLowerCase().includes(search.toLowerCase()) ||
      part.type.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div>
      <h2>庫存管理</h2>
      <input
        type="text"
        placeholder="搜尋零件名稱或類型"
        value={search}
        onChange={handleSearch}
        style={{ marginBottom: 16, padding: 8, width: 300 }}
      />
      <table border="1" cellPadding="8" style={{ width: "100%", marginTop: 16 }}>
        <thead>
          <tr>
            <th>名稱</th>
            <th>類型</th>
            <th>庫存數量</th>
            <th>價格</th>
            <th>操作</th>
          </tr>
        </thead>
        <tbody>
          {filteredParts.map((part) => (
            <tr key={part.id}>
              <td>{part.name}</td>
              <td>{part.type}</td>
              <td>{part.stock}</td>
              <td>{part.price}</td>
              <td>
                <button>編輯</button>
                <button style={{ marginLeft: 8 }}>刪除</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Inventory;
