import React, { useState } from 'react';

function AdminLogin() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage('');
    try {
      const res = await fetch('http://localhost:3001/api/admin/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, password })
      });
      const data = await res.json();
      if (res.ok) {
        localStorage.setItem('adminToken', data.token);
        setMessage('登入成功！');
        // 這裡可以導向到後台頁面
      } else {
        setMessage(data.message || '登入失敗');
      }
    } catch (err) {
      setMessage('伺服器錯誤');
    }
  };

  return (
    <div style={{ maxWidth: 400, margin: 'auto', padding: 20 }}>
      <h2>管理者登入</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" placeholder="帳號" value={username} onChange={e => setUsername(e.target.value)} required /><br />
        <input type="password" placeholder="密碼" value={password} onChange={e => setPassword(e.target.value)} required /><br />
        <button type="submit">登入</button>
      </form>
      {message && <div style={{ color: 'red', marginTop: 10 }}>{message}</div>}
    </div>
  );
}

export default AdminLogin;
