import { partsData } from "./partsData";
import React, { useState } from 'react';

const mockData = [
  { id: 1, name: "PO-9001",price: 260, image: "images/PO-9001.jpg" },
  { id: 2, name: "PO-9002",price: 260, image: "images/PO-9002.jpg" },
  { id: 3, name: "PO-9003",price: 260, image: "images/PO-9003.jpg" },
  { id: 4, name: "PO-9004",price: 260, image: "images/PO-9004.jpg" },
  { id: 5, name: "PO-9005",price: 260, image: "images/PO-9005.jpg" },
  { id: 6, name: "PO-9006",price: 260, image: "images/PO-9006.jpg" },
  { id: 7, name: "PO-9008",price: 220, image: "images/PO-9008.jpg" },
  { id: 8, name: "PO-9009",price: 260, image: "images/PO-9009.jpg" },
  { id: 9, name: "PO-9010",price: 260, image: "images/PO-9010.jpg" },
  { id: 10, name: "PO-9011",price: 260, image: "images/PO-9011.jpg" },
  { id: 11, name: "PO-9013",price: 220, image: "images/PO-9013.jpg" },
  { id: 12, name: "PO-9015",price: 260, image: "images/PO-9015.jpg" },
  { id: 13, name: "PO-9016",price: 220, image: "images/PO-9016.jpg" },
  { id: 14, name: "PO-9018",price: 220, image: "images/PO-9018.jpg" },
  { id: 15, name: "PO-9021",price: 220, image: "images/PO-9021.jpg" },
  { id: 16, name: "PO-9022",price: 220, image: "images/PO-9022.jpg" }
];

function getToday() {
  const d = new Date();
  // 取得 yyyy-mm-dd hh:mm:ss 格式
  const pad = n => n.toString().padStart(2, '0');
  return `${d.getFullYear()}-${pad(d.getMonth()+1)}-${pad(d.getDate())} ${pad(d.getHours())}:${pad(d.getMinutes())}:${pad(d.getSeconds())}`;
}

function ShippingStats() {
  const [shippingData, setShippingData] = useState(
    mockData.map(item => ({ ...item, quantity: '' }))
  );
  const today = getToday();

  const handleChange = (idx, value) => {
    const newData = [...shippingData];
    newData[idx].quantity = value;
    setShippingData(newData);
  };

  const [parts, setParts] = useState(partsData);
  const [selectedPartId, setSelectedPartId] = useState(parts[0]?.id || "");
  const [quantity, setQuantity] = useState(0);

  function handleSubmit(event) {
    event.preventDefault();
    // 依據每一筆商品的出貨數量同步扣除庫存
    const newParts = parts.map(part => {
      const shippingItem = shippingData.find(item => item.id === part.id);
      const qty = Number(shippingItem?.quantity) || 0;
      return {
        ...part,
        stock: part.stock - qty
      };
    });
    setParts(newParts);
    alert('發送完成！');
}

  return (
    <div style={{ textAlign: 'center', marginBottom: 16 }}>
      <img src="images/logo.png" alt="Logo" style={{ height: 150 }} />
      <div style={{ maxWidth: 600, margin: '0 auto', padding: 16 }}>
        <h2 style={{ textAlign: 'center' }}>銷售統計</h2>
        <div style={{ textAlign: 'center', marginBottom: 16, fontWeight: 'bold', fontSize: 28 }}>
          出貨日期：{today}
        </div>
        <form onSubmit={handleSubmit}>
          <table style={{ width: '100%', textAlign: 'center', verticalAlign: 'middle' }} className="center-table">
            <thead>
              <tr>
                <th>圖片</th>
                <th>品號</th>
                <th>售價</th>
                <th>出貨數量</th>
              </tr>
            </thead>
            <tbody>
              {parts.map((item, idx) => (
                <tr key={item.id}>
                  <td><img src={item.image} alt={item.name} style={{ width: 60, height: 60, objectFit: 'cover' }} /></td>
                  <td>{item.name}</td>
                  <td>{item.price}</td>
                  <td>{item.stock}</td> {/* 這裡顯示庫存 */}
                  <td>
                    <input
                      type="number"
                      min="0"
                      value={shippingData[idx]?.quantity || ''}
                      onChange={e => handleChange(idx, e.target.value)}
                      style={{ width: 60 }}
                    />
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          <div style={{ textAlign: 'center', marginTop: 16 }}>
            <button type="submit" style={{ fontSize: 24, padding: '12px 32px' }}>送出</button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default ShippingStats;
